draw(A--B--C--D--E--cycle ^^ Circle(O, abs(O-D)) ^^ A--O--D);
dot("$A$",A,SE);
dot("$B$",B,SE);
dot("$C$",C,NE);
dot("$D$",D,NE);
dot("$E$",E,W);
dot("$O$",O,dir(0));
[/asy]

